mapper.select:
SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* ,(SYSDATE-BDATE)*24 as dateb
			FROM (
					select * 
					from brd_tb 
					WHERE emp_no like #{empNo}
					order by no desc
					) a
			)
WHERE RN BETWEEN #{start} AND #{end}
order by no desc

SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					select * 
					from brd_scp_tb
					WHERE emp_no like #{empNo}
					order by no desc
					) a
			)
WHERE RN BETWEEN #{start} AND #{end}
order by no desc

mapper.delete:
           DELETE brd_scp_tb
            WHERE no = #{no}
			AND emp_no = #{empNo}
