mapper.select:
select ch_no
from ch_member_tb join emp_tb using(emp_no)
where user_id in #{fromId}
group by ch_no

select * from ch_info_tb I join (select ch_no,msg_date from ch_msg_tb where rowid in (select max(rowid) from ch_msg_tb group by ch_no)) using(ch_no) where ch_no in
#{item}
order by msg_date desc

mapper.update:
update ch_info_tb set ch_name = #{chName},
					  ch_ex = #{chEx} 
where ch_no = #{chNo}
