mapper.select:
SELECT EMP_NO,EMP_NAME, D.DEPT_NAME, J.JOB_NAME, (SELECT COUNT(*) FROM LATE_TB WHERE TAKEN BETWEEN TO_DATE(#{srcDateStart},'YYYY-MM-DD') AND TO_DATE(#{srcDateEnd},'YYYY-MM-DD') AND E.EMP_NO = EMP_NO) late_count, (SELECT COUNT(*) FROM LEAVE_TB WHERE TAKEN BETWEEN TO_DATE(#{srcDateStart},'YYYY-MM-DD') AND TO_DATE(#{srcDateEnd},'YYYY-MM-DD') AND E.EMP_NO = EMP_NO) leave_count, (SELECT COUNT(*) FROM ABSENT_TB WHERE TAKEN BETWEEN TO_DATE(#{srcDateStart},'YYYY-MM-DD') AND TO_DATE(#{srcDateEnd},'YYYY-MM-DD') AND E.EMP_NO = EMP_NO) absent_count FROM EMP_TB E LEFT JOIN DEPT_TB D USING (DEPT_CD) LEFT JOIN JOB_TB J USING (JOB_CD)
WHERE ${searchBy} IN #{keyword}
ORDER BY DEPT_CD

SELECT EMP_NO, USER_ID, EMP_NAME, D.DEPT_NAME, J.JOB_NAME FROM EMP_TB E LEFT JOIN DEPT_TB D USING (DEPT_CD) LEFT JOIN JOB_TB J USING (JOB_CD)
WHERE ${searchBy} IN #{keyword}
ORDER BY DEPT_CD

mapper.update:
	UPDATE EMP_TB 
	SET 
		EMP_EMAIL = #{empEmail}, EMP_CONTACT = #{empContact}
		, EMP_ADDRESS = #{empAddress} , EMP_MSG = #{empMsg}
		
		, EMP_IMAGE = #{empImage}
		
		
		, AUTHORITY = #{authority}
		
		 
	WHERE
		EMP_NO = #{empNo}
