mapper.select:
select * 
from sched_tb

	dept_name =

	 #{dept}


and emp_no in

${emp}



and sche_cate in

 #{type}

mapper.insert:
insert into sched_tb
values(
	seq_sche_schecode.nextval, #{scheCate}, #{empNo}, #{empName}, #{deptName},
	#{jobName}, #{scheName}, to_date(#{scheStart}, 'YYYY-MM-DD HH24:MI:SS'), 
	to_date(#{scheEnd}, 'YYYY-MM-DD HH24:MI:SS'), #{scheColor},#{scheEx}, #{alldayYn}
)
